using System.Collections;
using System.Collections.Generic;
using System.Runtime.InteropServices;
using UnityEngine;

public class EnemyManager : Singleton<EnemyManager>
{
    public GameObject RandomeEnemy=>enemyList.Count==0? null : enemyList[Random.Range(0,enemyList.Count)];
    //每波间隔时间
    public float TimeBetweenWaves=> timeBetweenWaves;
    [SerializeField]GameObject waveUI;
    [SerializeField]GameObject[] EnemyPrefabs;
    //生成间隔时间
    [SerializeField] float timeBetweenSpawns = 1f;
    //每波间隔时间
    [SerializeField] float timeBetweenWaves=1f;

    WaitForSeconds waitTimeBetweenSpawns;
    WaitForSeconds waitTimeBetweenWaves;

     int waveNumber = 1;
    public float WaveNumber => waveNumber;

    int enemyAmount;
    [SerializeField]int minEnemyAmount = 4;
    [SerializeField] int maxEnemyAmount = 10;

    public List<GameObject> enemyList;

    //
    WaitUntil waitUntilNoEnemy;
    //是否生成敌人
    [SerializeField]bool spawnEnermy;
    [SerializeField] GameObject bossPrefab;
    [SerializeField] int bossWaveNumber;

    protected override void Awake()
    {
        base.Awake();
        enemyList = new List<GameObject>();
        waitTimeBetweenSpawns=new WaitForSeconds(timeBetweenSpawns);
        waitUntilNoEnemy = new WaitUntil(() => enemyList.Count == 0);
        waitTimeBetweenWaves=new WaitForSeconds(timeBetweenWaves);
        
    }

    

    IEnumerator Start()
    {
        while (spawnEnermy&&GameManager.GameState!=GameState.GameOver)
        {     
            
            waveUI.SetActive(true);
            //每波间隔时间
            yield return waitTimeBetweenWaves;
            waveUI.SetActive(false);
            yield return StartCoroutine(nameof(RandomlySpawnCoroutine));
        }
       
    }
    IEnumerator RandomlySpawnCoroutine()
    {
        if (waveNumber%bossWaveNumber==0)
        {
           
            
            enemyList.Add(PoolManager.Release(bossPrefab));                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
            
        }
        else
        {
            enemyAmount = Mathf.Clamp(enemyAmount, minEnemyAmount + waveNumber /2, maxEnemyAmount);
            for (int i = 0; i < enemyAmount; i++)
            {

                enemyList.Add(PoolManager.Release(EnemyPrefabs[Random.Range(0, EnemyPrefabs.Length)]));
                yield return waitTimeBetweenSpawns;
            }
            
        }
        yield return waitUntilNoEnemy;
        waveNumber++;
    }

    //移出列表
    public void RemoveList(GameObject enemy) => enemyList.Remove(enemy);
    

}
